  const token = getToken();
  // const user = getUserFromToken();
  // return token && user && user.role === 'admin'
  //   ? children
  //   : <Navigate to="/login" />;
  if (!token || !isAdmin()) {
    return <Navigate to="/login" />;
  }

  return children;
};

export default AdminRoute;
