import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { toast } from 'react-toastify';

function Dashboard() {
  const [rides, setRides] = useState([]);
  const [pickup, setPickup] = useState('');
  const [drop, setDrop] = useState('');
  const [date, setDate] = useState('');
  const [filterPickup, setFilterPickup] = useState('');
  const [filterDrop, setFilterDrop] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const [sortOrder, setSortOrder] = useState('desc');

  // Fetch user's rides with filters and sorting
  const fetchRides = async () => {
    try {
      const params = {};
      if (filterPickup) params.pickup = filterPickup;
      if (filterDrop) params.drop = filterDrop;
      if (filterStatus) params.status = filterStatus;
      params.sortBy = 'date';
      params.order = sortOrder;
      const res = await axios.get('/rides', { params });
      setRides(res.data);
    } catch (err) {
      toast.error('Failed to fetch rides');
    }
  };

  useEffect(() => {
    fetchRides();
    // eslint-disable-next-line
  }, [filterPickup, filterDrop, filterStatus, sortOrder]);

  // Book a new ride
  const handleBook = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/rides', { pickup, drop, date });
      toast.success('Ride booked!');
      setPickup('');
      setDrop('');
      setDate('');
      fetchRides();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Booking failed');
    }
  };

  // Cancel a ride
  const handleCancel = async (id) => {
    if (!window.confirm('Cancel this ride?')) return;
    try {
      await axios.delete(`/rides/${id}`);
      toast.success('Ride cancelled');
      fetchRides();
    } catch (err) {
      toast.error('Cancel failed');
    }
  };

  return (
    <div className="container">
      <h2>User Dashboard</h2>
      <h3>Book a Ride</h3>
      <form onSubmit={handleBook} style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Pickup"
          value={pickup}
          onChange={e => setPickup(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Drop"
          value={drop}
          onChange={e => setDrop(e.target.value)}
          required
        />
        <input
          type="datetime-local"
          value={date}
          onChange={e => setDate(e.target.value)}
          required
        />
        <button type="submit">Book Ride</button>
      </form>

      <h3>Your Rides</h3>
      <div style={{ marginBottom: 10 }}>
        <input
          type="text"
          placeholder="Filter by Pickup"
          value={filterPickup}
          onChange={e => setFilterPickup(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <input
          type="text"
          placeholder="Filter by Drop"
          value={filterDrop}
          onChange={e => setFilterDrop(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <select
          value={filterStatus}
          onChange={e => setFilterStatus(e.target.value)}
          style={{ marginRight: 8 }}
        >
          <option value="">All Statuses</option>
          <option value="pending">Pending</option>
          <option value="approved">Approved</option>
          <option value="rejected">Rejected</option>
          <option value="cancelled">Cancelled</option>
        </select>
        <select
          value={sortOrder}
          onChange={e => setSortOrder(e.target.value)}
        >
          <option value="desc">Newest First</option>
          <option value="asc">Oldest First</option>
        </select>
      </div>
      <table border="1" cellPadding="6">
        <thead>
          <tr>
            <th>Pickup</th>
            <th>Drop</th>
            <th>Date</th>
            <th>Status</th>
            <th>Cancel</th>
          </tr>
        </thead>
        <tbody>
          {rides.length === 0 && (
            <tr>
              <td colSpan="5">No rides found.</td>
            </tr>
          )}
          {rides.map(ride => (
            <tr key={ride._id}>
              <td>{ride.pickup}</td>
              <td>{ride.drop}</td>
              <td>{new Date(ride.date).toLocaleString()}</td>
              <td>{ride.status}</td>
              <td>
                {(ride.status === 'pending' || ride.status === 'approved') ? (
                  <button onClick={() => handleCancel(ride._id)}>Cancel</button>
                ) : (
                  '-'
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;
