import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { toast } from 'react-toastify';

function AdminPanel() {
  const [rides, setRides] = useState([]);
  const [analytics, setAnalytics] = useState([]);
  const [filterPickup, setFilterPickup] = useState('');
  const [filterDrop, setFilterDrop] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const [filterUser, setFilterUser] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [sortOrder, setSortOrder] = useState('desc');
  const [loading, setLoading] = useState(false);
  const [analyticsLoading, setAnalyticsLoading] = useState(false);

  // Fetch all rides with filters and sorting
  const fetchRides = async () => {
    setLoading(true);
    try {
      const params = {};
      if (filterPickup) params.pickup = filterPickup;
      if (filterDrop) params.drop = filterDrop;
      if (filterStatus) params.status = filterStatus;
      if (filterUser) params.user = filterUser;
      if (filterDate) params.date = filterDate;
      params.sortBy = 'date';
      params.order = sortOrder;
      const res = await axios.get('/admin/rides', { params });
      setRides(res.data);
    } catch (err) {
      toast.error('Failed to fetch rides');
    } finally {
      setLoading(false);
    }
  };

  // Fetch analytics
  const fetchAnalytics = async () => {
    setAnalyticsLoading(true);
    try {
      const res = await axios.get('/admin/analytics');
      setAnalytics(res.data);
    } catch (err) {
      toast.error('Failed to fetch analytics');
    } finally {
      setAnalyticsLoading(false);
    }
  };

  useEffect(() => {
    fetchRides();
    // eslint-disable-next-line
  }, [filterPickup, filterDrop, filterStatus, filterUser, filterDate, sortOrder]);

  useEffect(() => {
    fetchAnalytics();
  }, []);

  // Approve a ride
  const handleApprove = async (id) => {
    try {
      await axios.post(`/admin/rides/${id}/approve`);
      toast.success('‚úÖ Ride approved successfully');
      fetchRides();
      fetchAnalytics();
    } catch (err) {
      toast.error('Failed to approve ride');
    }
  };

  // Reject a ride
  const handleReject = async (id) => {
    const reason = prompt('Reason for rejection:');
    if (!reason) return;
    try {
      await axios.post(`/admin/rides/${id}/reject`, { reason });
      toast.success('‚ùå Ride rejected successfully');
      fetchRides();
      fetchAnalytics();
    } catch (err) {
      toast.error('Failed to reject ride');
    }
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'pending': return 'status-pending';
      case 'approved': return 'status-approved';
      case 'rejected': return 'status-rejected';
      case 'cancelled': return 'status-cancelled';
      default: return 'status-pending';
    }
  };

  return (
    <div className="container">
      <div className="main-content fade-in">
        <div className="hero">
          <h1>‚öôÔ∏è Admin Dashboard</h1>
          <p>Manage and monitor all corporate ride bookings</p>
        </div>

        {/* Analytics Section */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">üìä Ride Analytics (Last 7 Days)</h2>
          </div>
          
          {analyticsLoading ? (
            <div className="loading">
              <div className="spinner"></div>
              <span style={{ marginLeft: '1rem' }}>Loading analytics...</span>
            </div>
          ) : (
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
              {analytics.map(a => (
                <div key={a._id} style={{ 
                  background: 'linear-gradient(135deg, var(--primary-color) 0%, var(--primary-dark) 100%)',
                  color: 'white',
                  padding: '1.5rem',
                  borderRadius: 'var(--border-radius-sm)',
                  textAlign: 'center'
                }}>
                  <h3 style={{ margin: '0 0 0.5rem 0', fontSize: '1.2rem' }}>{a._id}</h3>
                  <p style={{ margin: 0, fontSize: '2rem', fontWeight: 'bold' }}>{a.count}</p>
                  <p style={{ margin: '0.5rem 0 0 0', opacity: 0.9 }}>rides</p>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* All Rides Section */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">üìã All Rides</h2>
          </div>
          
          {/* Filters */}
          <div className="filters">
            <div className="filter-group">
              <label>üìç Pickup:</label>
              <input
                type="text"
                className="filter-input"
                placeholder="Filter by pickup"
                value={filterPickup}
                onChange={e => setFilterPickup(e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>üéØ Drop:</label>
              <input
                type="text"
                className="filter-input"
                placeholder="Filter by drop"
                value={filterDrop}
                onChange={e => setFilterDrop(e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>üë§ User:</label>
              <input
                type="text"
                className="filter-input"
                placeholder="Filter by user"
                value={filterUser}
                onChange={e => setFilterUser(e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>üìÖ Date:</label>
              <input
                type="date"
                className="filter-input"
                value={filterDate}
                onChange={e => setFilterDate(e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>üìä Status:</label>
              <select
                className="filter-select"
                value={filterStatus}
                onChange={e => setFilterStatus(e.target.value)}
              >
                <option value="">All Statuses</option>
                <option value="pending">Pending</option>
                <option value="approved">Approved</option>
                <option value="rejected">Rejected</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>üìÖ Sort:</label>
              <select
                className="filter-select"
                value={sortOrder}
                onChange={e => setSortOrder(e.target.value)}
              >
                <option value="desc">Newest First</option>
                <option value="asc">Oldest First</option>
              </select>
            </div>
          </div>

          {/* Rides Table */}
          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
              <span style={{ marginLeft: '1rem' }}>Loading rides...</span>
            </div>
          ) : (
            <div className="table-container">
              <table className="table">
                <thead>
                  <tr>
                    <th>üìç Pickup</th>
                    <th>üéØ Drop</th>
                    <th>üìÖ Date & Time</th>
                    <th>üìä Status</th>
                    <th>üë§ User</th>
                    <th>‚ö° Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {rides.length === 0 ? (
                    <tr>
                      <td colSpan="6" className="empty-state">
                        <i>üöó</i>
                        <p>No rides found.</p>
                      </td>
                    </tr>
                  ) : (
                    rides.map(ride => (
                      <tr key={ride._id}>
                        <td>{ride.pickup}</td>
                        <td>{ride.drop}</td>
                        <td>{new Date(ride.date).toLocaleString()}</td>
                        <td>
                          <span className={`status-badge ${getStatusBadgeClass(ride.status)}`}>
                            {ride.status}
                          </span>
                        </td>
                        <td>{ride.user?.name || ride.user?.email || ride.user?._id}</td>
                        <td>
                          {ride.status === 'pending' ? (
                            <div style={{ display: 'flex', gap: '0.5rem' }}>
                              <button 
                                onClick={() => handleApprove(ride._id)}
                                className="btn btn-success"
                                style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                              >
                                <i>‚úÖ</i> Approve
                              </button>
                              <button 
                                onClick={() => handleReject(ride._id)}
                                className="btn btn-danger"
                                style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                              >
                                <i>‚ùå</i> Reject
                              </button>
                            </div>
                          ) : (
                            <span style={{ color: '#6c757d' }}>No actions available</span>
                          )}
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminPanel;
