import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { toast } from 'react-toastify';

function Dashboard() {
  const [rides, setRides] = useState([]);
  const [pickup, setPickup] = useState('');
  const [drop, setDrop] = useState('');
  const [date, setDate] = useState('');
  const [filterPickup, setFilterPickup] = useState('');
  const [filterDrop, setFilterDrop] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const [sortOrder, setSortOrder] = useState('desc');
  const [loading, setLoading] = useState(false);
  const [bookingLoading, setBookingLoading] = useState(false);

  // Fetch user's rides with filters and sorting
  const fetchRides = async () => {
    setLoading(true);
    try {
      const params = {};
      if (filterPickup) params.pickup = filterPickup;
      if (filterDrop) params.drop = filterDrop;
      if (filterStatus) params.status = filterStatus;
      params.sortBy = 'date';
      params.order = sortOrder;
      const res = await axios.get('/rides', { params });
      setRides(res.data);
    } catch (err) {
      toast.error('Failed to fetch rides');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRides();
    // eslint-disable-next-line
  }, [filterPickup, filterDrop, filterStatus, sortOrder]);

  // Book a new ride
  const handleBook = async (e) => {
    e.preventDefault();
    setBookingLoading(true);
    try {
      await axios.post('/rides', { pickup, drop, date });
      toast.success('üöó Ride booked successfully!');
      setPickup('');
      setDrop('');
      setDate('');
      fetchRides();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Booking failed');
    } finally {
      setBookingLoading(false);
    }
  };

  // Cancel a ride
  const handleCancel = async (id) => {
    if (!window.confirm('Are you sure you want to cancel this ride?')) return;
    try {
      await axios.delete(`/rides/${id}`);
      toast.success('‚úÖ Ride cancelled successfully');
      fetchRides();
    } catch (err) {
      toast.error('Failed to cancel ride');
    }
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'pending': return 'status-pending';
      case 'approved': return 'status-approved';
      case 'rejected': return 'status-rejected';
      case 'cancelled': return 'status-cancelled';
      default: return 'status-pending';
    }
  };

  return (
    <div className="container">
      <div className="main-content fade-in">
        <div className="hero">
          <h1>üöó Your Ride Dashboard</h1>
          <p>Book, track, and manage your corporate rides</p>
        </div>

        {/* Book a Ride Section */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">üìÖ Book a New Ride</h2>
          </div>
          
          <form onSubmit={handleBook}>
            <div className="form-row">
              <div className="form-group">
                <label className="form-label">üìç Pickup Location</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter pickup location"
                  value={pickup}
                  onChange={e => setPickup(e.target.value)}
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">üéØ Drop Location</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter drop location"
                  value={drop}
                  onChange={e => setDrop(e.target.value)}
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">üìÖ Date & Time</label>
                <input
                  type="datetime-local"
                  className="form-input"
                  value={date}
                  onChange={e => setDate(e.target.value)}
                  required
                />
              </div>
            </div>
            
            <button 
              type="submit" 
              className="btn btn-primary"
              disabled={bookingLoading}
            >
              {bookingLoading ? (
                <>
                  <div className="spinner" style={{ width: '20px', height: '20px' }}></div>
                  Booking Ride...
                </>
              ) : (
                <>
                  <i>üöÄ</i>
                  Book Ride
                </>
              )}
            </button>
          </form>
        </div>

        {/* Your Rides Section */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">üìã Your Rides</h2>
          </div>
          
          {/* Filters */}
          <div className="filters">
            <div className="filter-group">
              <label>üìç Pickup:</label>
              <input
                type="text"
                className="filter-input"
                placeholder="Filter by pickup"
                value={filterPickup}
                onChange={e => setFilterPickup(e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>üéØ Drop:</label>
              <input
                type="text"
                className="filter-input"
                placeholder="Filter by drop"
                value={filterDrop}
                onChange={e => setFilterDrop(e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>üìä Status:</label>
              <select
                className="filter-select"
                value={filterStatus}
                onChange={e => setFilterStatus(e.target.value)}
              >
                <option value="">All Statuses</option>
                <option value="pending">Pending</option>
                <option value="approved">Approved</option>
                <option value="rejected">Rejected</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>üìÖ Sort:</label>
              <select
                className="filter-select"
                value={sortOrder}
                onChange={e => setSortOrder(e.target.value)}
              >
                <option value="desc">Newest First</option>
                <option value="asc">Oldest First</option>
              </select>
            </div>
          </div>

          {/* Rides Table */}
          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
              <span style={{ marginLeft: '1rem' }}>Loading rides...</span>
            </div>
          ) : (
            <div className="table-container">
              <table className="table">
                <thead>
                  <tr>
                    <th>üìç Pickup</th>
                    <th>üéØ Drop</th>
                    <th>üìÖ Date & Time</th>
                    <th>üìä Status</th>
                    <th>‚ö° Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {rides.length === 0 ? (
                    <tr>
                      <td colSpan="5" className="empty-state">
                        <i>üöó</i>
                        <p>No rides found. Book your first ride above!</p>
                      </td>
                    </tr>
                  ) : (
                    rides.map(ride => (
                      <tr key={ride._id}>
                        <td>{ride.pickup}</td>
                        <td>{ride.drop}</td>
                        <td>{new Date(ride.date).toLocaleString()}</td>
                        <td>
                          <span className={`status-badge ${getStatusBadgeClass(ride.status)}`}>
                            {ride.status}
                          </span>
                        </td>
                        <td>
                          {(ride.status === 'pending' || ride.status === 'approved') ? (
                            <button 
                              onClick={() => handleCancel(ride._id)}
                              className="btn btn-danger"
                              style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                            >
                              <i>‚ùå</i> Cancel
                            </button>
                          ) : (
                            <span style={{ color: '#6c757d' }}>-</span>
                          )}
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
